use File::Basename;
use Cwd;
use Socket;

use strict;
use warnings;

use constant REMOTE_IP      => "10.8.4.39";
use constant REMOTE_PORT    => "21";        #FTP
use constant LARGE_4k_BUF   => "";
use constant LARGE_4k_BIN   => "\x7f\x80\x80\xbf\x01\x02\x03\x04\x05\x06\x07\x08\x09\x0a\x0b\x0c\x0d\x0f\x7f\x01\x7f\xff\x7f\x7f\x5a\x5b\x50\x5c\x01\x02\x03\x04\x05\x06\x07\x08\x09\x0a\x0b\x0c\x0d\x0f\x7f\x80\x80\x01\x7f\x7f\x7f\x5a\x5b\x50\x5c\x01\x02\x03\x04\x05\x06\x07\x08\x09\x0a\x0b\x0c\x0d\x0f\x7f\x01\x7f\xff\x7f\x7f\x5a\x5b\x50\x5c\x01\x02\x03\x04\x05\x06\x07\x08\x09\x0a\x0b\x0c\x0d\x0f\x7f\x01\x7f\xff\x7f\x7f\x5a\x5b\x50\x5c\x01\x02\x03\x04\x05\x06\x07\x08\x09\x0a\x0b\x0c\x0d\x0f\x7f\x01\x7f\xff\x7f\x7f\x5a\x5b\x50\";

my $port     = REMOTE_PORT;
my $host     = REMOTE_IP;
my $garbage1 = LARGE_4k_BUF;
my $garbage2 = LARGE_4k_BIN;
my $line;


# create the socket, connect to the port
socket(my $SOCKET,PF_INET,SOCK_STREAM,(getprotobyname('tcp'))[2])
   or die "Can't create a socket $!\n";
connect($SOCKET, pack_sockaddr_in($port, inet_aton($host)))
   or die "Can't connect to $host : $port! \n";

print "Connected to $host : $port\r\n";
$line = <$SOCKET>; print "$line\n";

send( $SOCKET, "PASV\r\n", 0);
$line = <$SOCKET>; print "$line\n";
send( $SOCKET, "LIST /\r\n", 0);
$line = <$SOCKET>; print "$line\n";
send( $SOCKET, "EHLO rapid7.com\r\n", 0);
$line = <$SOCKET>; print "$line\n";
send( $SOCKET, "AUTH TLS\r\n", 0);
$line = <$SOCKET>; print "$line\n";
send( $SOCKET, "$garbage1\r\n", 0);
$line = <$SOCKET>; print "$line\n";
send( $SOCKET, "$garbage2\r\n", 0);
$line = <$SOCKET>; print "$line\n";

   
#my $line;
#while ($line = <SOCKET>) {
#        print "$line\n";
#}

print "Done\r\n";
close $SOCKET or die "close: $!";